/* ___ Player ___ */

/* Ad */
#page_layout .audio_page_layout .CatalogSection:not(.CatalogSection--divided) {
	display: none;
}

/* VK Next - start */
.audio_page_player2 .audio_page_player_ctrl.audio_page_player_btns {
	float: right;
	padding: 0 14px 0 5px;
	display: -webkit-box;
	display: -ms-flexbox;
	display: flex;
	-webkit-box-align: center;
	-ms-flex-align: center;
	align-items: center;
	height: 56px;
}
html body .audio_page_player_volume_wrap {
	position: absolute;
	bottom: 23px;
	right: 12px;
}
html body .audio_page_player_btns {
	position: absolute;
	bottom: 0;
	width: 100%;
	display: flex;
	justify-content: center;
}
/* VK Next - end */

/* animation for shadow */
@keyframes sizeBig {
	from {
		transform: scale(1.2);
		opacity: 0.5;
	}
	20% {
		transform: scale(1.8);
		filter: blur(9px);
		border-radius: 30px;
		opacity: 0.7;
	}
	40% {
		transform: scale(1.2);
		opacity: 0.5;
	}
	60% {
		transform: scale(1.8) translateX(-8px);
		filter: blur(9px);
		border-radius: 30px;
		opacity: 0.7;
	}
	to {
		transform: scale(1.2);
		opacity: 0.5;
	}
}

@keyframes sizeBig_CoverHeader {
	from {
		transform: scale(1.2);
		opacity: 0.5;
	}
	20% {
		transform: scale(1.8) translate(2px);
		filter: blur(9px);
		border-radius: 30px;
		opacity: 0.7;
	}
	40% {
		transform: scale(1.2);
		opacity: 0.5;
	}
	60% {
		transform: scale(1.8) translate(2px);
		filter: blur(9px);
		border-radius: 30px;
		opacity: 0.7;
	}
	to {
		transform: scale(1.2);
		opacity: 0.5;
	}
}

@keyframes sizeLite {
	from {
		transform: scale(0.7);
	}
	50% {
		transform: scale(1.1);
		z-index: 10;
	}
	to {
		transform: scale(0.7);
	}
}

/* mini cover in playlist */
.audio_w_covers .audio_row .audio_row__cover,
.audio_w_covers .audio_row .audio_row__cover_back {
	border-radius: 6px;
}

[dir]
	body
	.audio_page_layout2
	.audio_page_player2
	.audio_page_player__cover:not([style*='background-image'])::before {
	transition: all 1s;
	animation: rotate 10s ease infinite;
}

html body .audio_page_layout2 .audio_page_player_wrap,
html body .audio_page_player2 {
	height: 120px;
	padding-bottom: 16px;
	position: relative;
	z-index: 10;
}

#page_layout .audio_page_layout2 .audio_page_player_wrap {
	background: transparent;
	box-shadow: none;
}

.audio_page_player2 .audio_page_player__cover {
	background-color: var(--vkui--color_background_content);
}

/* player fixed */
.audio_page_player2.audio_page_player_fixed {
	top: auto;
	bottom: 16px;
	position: fixed;
	z-index: 102;
	margin: 0;
	border-radius: 0;
	-webkit-transition: -webkit-transform 250ms ease;
	transition: -webkit-transform 250ms ease;
	transition: transform 250ms ease;
	transition: transform 250ms ease, -webkit-transform 250ms ease;
	-webkit-box-shadow: 0 1px 0 0 var(--black_alpha12);
	box-shadow: 0 1px 0 0 var(--black_alpha12);
	-webkit-transform: translate(0, 10px);
	transform: translate(0, -7px);
	background: transparent;
	padding-bottom: 0px;
}

.audio_page_player2.audio_page_player_fixed:after {
	content: '';
	display: block;
	position: absolute;
	top: 32px;
	left: 0px;
	height: 200px;
	width: 100%;
	background-color: var(--vkui--color_background);
	z-index: -1;
	visibility: visible;
	filter: blur(20px);
	transform: scale(1.55) rotate(2deg);
	/*transition: all .5s;*/
}

.audio_section {
	padding-bottom: 125px;
}

.audio_page_player2.audio_page_player_fixed .VKNextU-cover-shadow {
	top: 8px;
