}


@keyframes sizeBig {
	from {
		transform: scale(1.2); 
		/*translate(0, 0, 0);*/
		opacity: .5;
	}
	20% {
		transform: scale(1.8); 
		/*translate(10px, 0, 0);*/
		filter: blur(9px);
		border-radius: 30px;
		opacity: .7;
	}
	40% {
		transform: scale(1.2); 
		/*translate(10px, 12px, 0);*/
		opacity: .5;
	}
	60% {
		transform: scale(1.8) translateX(-8px);
		/*translate(-10px, 0, 0);*/
		filter: blur(9px);
		border-radius: 30px;
		opacity: .7;
	}
	to {
		transform: scale(1.2); 
		/*translate(0, 0, 0);*/
		opacity: .5;
	}
}

@keyframes sizeBig_CoverHeader {
	from {
		transform: scale(1.2); 
		/*translate(0, 0, 0);*/
		opacity: .5;
	}
	20% {
		transform: scale(1.8) translate(2px); 
		/*translate(10px, 0, 0);*/
		filter: blur(9px);
		border-radius: 30px;
		opacity: .7;
	}
	40% {
		transform: scale(1.2); 
		/*translate(10px, 12px, 0);*/
		opacity: .5;
	}
	60% {
		transform: scale(1.8) translate(2px);
		/*translate(-10px, 0, 0);*/
		filter: blur(9px);
		border-radius: 30px;
		opacity: .7;
	}
	to {
		transform: scale(1.2); 
		/*translate(0, 0, 0);*/
		opacity: .5;
	}
}

@keyframes sizeLite {
	from {
		transform: scale(.7);
	}
	50% {
		transform: scale(1.1);
		z-index: 10;
	}
	to {
		transform: scale(.7);
	}
}

/* mini cover in playlist */
.audio_w_covers .audio_row .audio_row__cover, .audio_w_covers .audio_row .audio_row__cover_back {
    border-radius: 6px;
}

/*==================*/

[dir] body .audio_page_layout2 .audio_page_player2 .audio_page_player__cover:not([style*=background-image])::before {
	transition: all 1s;
	animation: rotate 10s ease infinite;
}


/* = Redesign Player */

html body .audio_page_layout2 .audio_page_player_wrap,
html body .audio_page_player2 {
    height: 120px;
    padding-bottom: 16px;
    position: relative;
    z-index: 10;
}

#page_layout .audio_page_layout2 .audio_page_player_wrap {
    background: transparent;
    box-shadow: none;
}

.audio_page_player2 .audio_page_player__cover {
    background-color: var(--vkui--color_background_content);
}

/* player fixed */
.audio_page_player2.audio_page_player_fixed {
	top: auto;
	bottom: 16px;
    position: fixed;
    z-index: 102;
    margin: 0;
    border-radius: 0;
    -webkit-transition: -webkit-transform 250ms ease;
    transition: -webkit-transform 250ms ease;
    transition: transform 250ms ease;
    transition: transform 250ms ease,-webkit-transform 250ms ease;
    -webkit-box-shadow: 0 1px 0 0 var(--black_alpha12);
    box-shadow: 0 1px 0 0 var(--black_alpha12);
    -webkit-transform: translate(0,10px);
    transform: translate(0, -7px);
    background: transparent;
    padding-bottom: 0px;
}

.audio_page_player2.audio_page_player_fixed:after {
	content: "";
    display: block;
    position: absolute;
    top: 32px;
    left: 0px;
    height: 200px;
    width: 100%;
    background-color: var(--vkui--color_background);
    z-index: -1;
    visibility: visible;
    filter: blur(20px);
    transform: scale(1.55) rotate(2deg);
	/*transition: all .5s;*/
}

.audio_section {
    padding-bottom: 125px;
}

.audio_page_player2.audio_page_player_fixed .VKNextU-cover-shadow {
	top: 8px;
}

.audio_page_player2.audio_page_player_fixed .audio_page_player__cover._audio_page_player__cover:hover {
    transform: translate(11px, -8px) scale(1.5);
    border-radius: 3px ;
    /* border: 1px solid #f1f1f126; */
    transition: all .7s ;
    z-index: 999999 ;
}

/*.audio_page_player2.audio_page_player_fixed .audio_page_player_play {*/
/*	transform: translateX(124px);*/
/*}*/

.audio_page_player2.audio_page_player_fixed .audio_page_player_play,
.audio_page_player2.audio_page_player_fixed .audio_page_player_prev, 
.audio_page_player2.audio_page_player_fixed .audio_page_player_next {
	transform: translateX(58px); 
	transform: translateX(58px);
}

.audio_page_player2.audio_page_player_fixed .audio_page_player_play,
.audio_page_player2.audio_page_player_fixed .audio_page_player_prev, 
.audio_page_player2.audio_page_player_fixed .audio_page_player_next {
    bottom: -2px; 
    /* +top */
}

.audio_page_player2.audio_page_player_fixed .audio_page_player_repeat,
.audio_page_player2.audio_page_player_fixed .audio_page_player_shuffle,
.audio_page_player2.audio_page_player_fixed .audio_page_player_recoms,
.audio_page_player2.audio_page_player_fixed .audio_page_player_share {
  top: 16.5px;
  /* -top */
}

