.audio_page_player2.audio_page_player_fixed .audio_page_player_btn_lyrics,
.audio_page_player2.audio_page_player_fixed .audio_page_player_status {
	position: absolute;
    left: 50%;
    top: 16px;
    /*bottom: 8px;*/
    z-index: 99999;
	width: 24px;
    height: 24px;
