/* ___ EMOJI ____ */

/* FOR EDIT STYLES WITH DEVTOOLS */
/*.StickerPickerViewer {*/
/*	opacity: 1;*/
/*}*/

.StickerPickerViewer
> div > div > div > div
> div:nth-child(1) > div:nth-child(1)
> div > div:nth-child(2) > div {
   --emoji-size: 32px;
   --emoji-padding: 16px;
   --emoji-columns: 8;
}

/* container */
.StickerPickerViewer
> div > div > div > div
> div:nth-child(1) > div:nth-child(1)
> div > div:nth-child(2) > div > div > div {
   margin-bottom: 2px;
   gap: 8px 10px ;
   justify-content: center;
   display: flex ;
   text-align: center;
   flex-wrap: wrap;
}

/* header */
.StickerPickerViewer
> div > div > div > div
> div:nth-child(1) > div:nth-child(1)
> div > div:nth-child(2) > div > div
> div[data-header="true"],
.StickerPickerViewer h5 {
   font-weight: 500;
	margin-bottom: 0;
	display: flex ;
	justify-content: left;
	text-align: center;
	flex-wrap: wrap;
	gap: 0;
}

/* element */
.StickerPickerViewer
> div > div > div > div
> div:nth-child(1) > div:nth-child(1)
> div > div:nth-child(2) > div > div > div > div {
   width: 32px;
   height: 32px;
}

.StickerPickerViewer
> div > div > div > div
> div:nth-child(1) > div:nth-child(1)
> div > div:nth-child(2) > div > div > div i {
	background-size: 32px;
	line-height: 32px;
	font-size: 32px;
}

.StickerPickerViewer
> div > div > div > div
> div:nth-child(1) > div:nth-child(1)
> div > div:nth-child(2) > div > div {
   width: 100%;
   padding-top: 0;
}

.StickerPickerViewer
> div > div > div > div >
div:nth-child(2) > div {
	padding: 0 14px;
}

.StickerPickerViewer
> div > div > div > div >
div:nth-child(2) > div::after {
   content: 'Раздел';
   margin-left: 8px;
   margin-top: 3px;
   font-weight: 500;
}

.StickerPickerViewer
> div > div > div > div >
div:nth-child(2) > div:nth-child(1)::after {
	content: 'Эмодзи';
}

.StickerPickerViewer
> div > div > div > div >
div:nth-child(2) > div:nth-child(2)::after {
	content: 'Стикеры';
}

.StickerPickerViewer
> div > div > div > div >
div:nth-child(2) > div:nth-child(3)::after {
   content: 'Магазин';
}


